export default class GameMap {
  current = Maps.route1;
  collisions: Array<Array<number>>;
  collisionMap: any;

  constructor(public parent: Game) {}

  // TODO: map type?
  async load() {
    // Our map controls
    let drag = false;
    const dragStart = {
      x: 0,
      y: 0,
    };
    const dragEnd = {
      x: 0,
      y: 0,
    };
    document.addEventListener('mousedown', () => {
      if (MyApp.cursor.inBounds(0, 0, window.innerWidth, this.current.height * MAP_TILE_SIZE)) {
        dragStart.x = MyApp.cursor.x;
        dragStart.y = MyApp.cursor.y;
        drag = true;
      }
    });
    document.addEventListener('mousemove', () => {
      if (drag) {
        dragEnd.x = MyApp.cursor.x;
        dragEnd.y = MyApp.cursor.y;
        const movementX = Math.abs(dragEnd.x - dragStart.x);
        const movementY = Math.abs(dragEnd.y - dragStart.y);
        if (!(movementX + movementY)) return;
        // Ensure our camera doesn't go out of the map
        Settings.camera.x = Math.max(0, Math.min(this.current.width * MAP_TILE_SIZE - canvas.width, Settings.camera.x + (dragEnd.x > dragStart.x ? -movementX : movementX)));
        Settings.camera.y = Math.max(0, Math.min(this.current.height * MAP_TILE_SIZE - canvas.height, Settings.camera.y + (dragEnd.y > dragStart.y ? -movementY : movementY)));
        dragStart.x = dragEnd.x;
        dragStart.y = dragEnd.y;
      }
    });
    document.addEventListener('mouseup', () => {
      drag = false;
    });
    document.addEventListener('wheel', (event) => {
      const movement = event.deltaY > 0 ? 0.1 : -0.1;
      if (!movement) return;
      // TODO: calc max zoom distance based on current map size
      // TODO: Move camera x/y if zoomed out too far showing no map
      Settings.camera.z = Math.max(0.1, Math.min(1, Settings.camera.z + movement));
      zoom(Settings.camera.z);
    });
  }

  // TODO: map type?
  async setMap(map: string) {
    this.current = Maps[map];
    this.current.image = await loadImage(`./assets/images/map/${map}.png`);
    this.current.image_top = await loadImage(`./assets/images/map/${map}_top.png`);
    Settings.camera.x = 0;
    Settings.camera.y = 0;
    this.updateCollisionMap();
  }

  updateCollisionMap() {
    this.collisions = [...this.current.collisions.map((a) => [...a])];
    this.collisionMap = new PF.Grid(this.collisions);
  }
}
